window.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("#modal-trigger");document.querySelectorAll(".display-modal");t.forEach(function(t){t.addEventListener("click",function(){var e=t.dataset.trigger,o=document.querySelector("."+e);window.scrollY;o.style.width="100vw",o.style.top=window.scrollY+"px",document.body.classList.add("stop-scrolling"),o.addEventListener("click",function(){o.style.width="0",document.body.classList.remove("stop-scrolling")})})})});